[3837] = {id = 3837,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 要不是老爷派我出来见见世面，我才不会离开呢？你就尽管安心前去吧！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101310]]}}}}},
