[3840] = {id = 3840,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 这又是哪位大爷大奶来烦我来了？ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101320]]}}}}},
[3841] = {id = 3841,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 小老儿就是这里的土地公！这是哪？福临山啊，一个原本和名字一样有福气的地方。 ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[101320]]}}}}},
[3842] = {id = 3842,total_content = 1,content = {[1] = {total_list = 1,list = {[1] = {type = 0,text = [[ 神位小，什么妖魔鬼怪都能驱使我！ ]],other = [[]]}}}}},
[3843] = {id = 3843,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 为什么是原来？因为自从天蓬元帅被贬投胎在此后，灾难就源源不断了！你呀，还是问问那个朱一霸最清楚了！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101330]]}}}}},
