[3852] = {id = 3852,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 我叫朱一霸，不叫猪尾巴，如果你再欺负我，我就让我干娘收了你！娘，你在哪里啊，快帮我揍他！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101360]]}}}}},
[3853] = {id = 3853,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 嘘···仙家小声点，从那边悄悄绕过来。 ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[101360]]}}}}},
