[3855] = {id = 3855,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 你可别惹恼那个一霸，他是云栈洞卯二娘的义子。你问卯二娘是谁？她就是我们高老庄那个猪妖姑爷的大老婆！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101370]]}}}}},
[3856] = {id = 3856,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 那个猪妖，当初变化成人样骗过了我们所有人，真是太可恨了！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[101370]]}}}}},
