[3858] = {id = 3858,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 什么天蓬元帅下凡，根本就是一个骗财骗色的，这种妖怪居然还能跟随大唐高僧西天取经，我看这经估计也没什么料！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101380]]}}}}},
[3859] = {id = 3859,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 当初老爷看猪刚鬣力气大，肯吃苦，家中没有妻小才要招他为婿，谁知道他醉酒露出本像是个丑猪妖不说，还有一个大老婆。 ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[101380]]}}}}},
[3860] = {id = 3860,total_content = 1,content = {[1] = {total_list = 1,list = {[1] = {type = 0,text = [[ 人不风流枉少年，我有多么纯粹的一个少年心！ ]],other = [[]]}}}}},
[3861] = {id = 3861,total_content = 2,content = {[1] = {total_list = 1,list = {[1] = {type = 0,text = [[ 自从猪八戒西去取经，他的大老婆卯二娘天天来庄中闹，说让高老太爷把小姐许配给他的猪妖义子，好补偿安慰她失夫之痛！ ]],other = [[]]}}},[2] = {total_list = 2,list = {[1] = {type = 1,text = [[ 岂有此理，让我教训一下她！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101390]]}}}}},
