[6658] = {id = 6658,total_content = 1,content = {[1] = {total_list = 1,list = {[1] = {type = 0,text = [[ 人家可是高老庄一枝花！ ]],other = [[]]}}}}},
[6659] = {id = 6659,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 长安城有个带着老虎四处跑的小丫头，麻烦帮我警告她一下，不要用老虎伤人，否则必定受到严惩！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101271]]}}}}},
[6660] = {id = 6660,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 不管老虎还是狮子，在我看来都和猫咪一样可爱！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[101271]]}}}}},
[6662] = {id = 6662,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 客官先慢开口，在下刚刚和那个渔夫说的我口干舌燥，能不能麻烦你先到那边给我买壶茶来？ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101281]]}}}}},
[6663] = {id = 6663,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 店家暂离，请客官自己交钱沏茶！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[101281]]}}}}},
[6664] = {id = 6664,total_content = 1,content = {[1] = {total_list = 1,list = {[1] = {type = 0,text = [[ 人啊，再怎么厉害还是要喝水的！ ]],other = [[]]}}}}},
[6671] = {id = 6671,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ （桌上字条）沏好的茶都放在那边，客官请交钱后自己端茶享用！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101284]]}}}}},
[6672] = {id = 6672,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 恩，不错不错，还是当年的味道！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[101284]]}}}}},
