[6674] = {id = 6674,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 看你面相，的确和传说中的齐天大圣有师徒缘分，但这个缘分现在还没到！不如等等看也许机会自然来！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101285]]}}}}},
[6675] = {id = 6675,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 我乃秦琼，字叔宝！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[101285]]}}}}},
[6676] = {id = 6676,total_content = 1,content = {[1] = {total_list = 1,list = {[1] = {type = 0,text = [[ 时候未到，天机不可泄露！ ]],other = [[]]}}}}},
[6677] = {id = 6677,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 听袁大师说，仙家还会在长安待上一段时间，不如随我四处平乱吧！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101286]]}}}}},
[6678] = {id = 6678,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 来，我教你领平乱任务！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[101286]]}}}}},
[6680] = {id = 6680,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 泾河龙王让我救他一命，我以为把魏征留在宫中即可，奈何他在睡梦中行刑。此后，这条龙一直在梦中向我索命，这可如何是好？ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101294]]}}}}},
[6681] = {id = 6681,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 为了主上，我和秦琼愿一身戎装在殿外守卫！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[101294]]}}}}},
[6682] = {id = 6682,total_content = 1,content = {[1] = {total_list = 1,list = {[1] = {type = 0,text = [[ 恶龙吓的我夜不能寐~ ]],other = [[]]}}}}},
[6694] = {id = 6694,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 那泾河龙王被魏征大人斩杀后，阴魂一直不散，要加害天子性命，麻烦你通知尉迟将军赶快进宫护卫！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101288]]}}}}},
[6695] = {id = 6695,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 我一生戎马疆场，杀人无数，何惧妖邪！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[101288]]}}}}},
