[7101] = {id = 7101,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 我···我就是云儿。半月前被那帮猪妖捉到了云栈洞，卯二娘痛恨我是高老庄的人，就用尽酷刑虐待，还···还把我扔给了那帮小猪妖！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101520]]}}}}},
[7102] = {id = 7102,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 我几次逃跑，都被抓了回去，最后一次，卯二娘施法让我变成了老婆婆。 ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[101520]]}}}}},
[7103] = {id = 7103,total_content = 1,content = {[1] = {total_list = 1,list = {[1] = {type = 0,text = [[ 让我死了得了··· ]],other = [[]]}}}}},
[7104] = {id = 7104,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 仙家，我这辈子是无颜再见高兴哥哥了，就请把这根银簪带给他，说我已经死了吧！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101521]]}}}}},
[7105] = {id = 7105,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 此生难再相守，只能来世再聚了！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[101521]]}}}}},
[7106] = {id = 7106,total_content = 1,content = {[1] = {total_list = 1,list = {[1] = {type = 0,text = [[ 今生难相守，只能期待来世了~ ]],other = [[]]}}}}},
[7107] = {id = 7107,total_content = 1,content = {[1] = {total_list = 3,list = {[1] = {type = 0,text = [[ 仙家，你说你就能捉妖，那我考考你怎么样？首先是文考。请听题：爷爷的孙子的爸爸的儿子的爷爷的儿子是爷爷的谁？ ]],other = [[]]},[2] = {type = 5,text = [[ 是爷爷的儿子 ]],other = [[101522]]},[3] = {type = 43,text = [[ 是爷爷的爸爸 ]],other = [[]]}}}}},
[7108] = {id = 7108,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 恩恩，看来脑筋还是比较有用，没白长个大头。 ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[101522]]}}}}},
