[7110] = {id = 7110,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 既然收妖怪，那法术自然是一定要高才行。你去抓些云栈洞妖过来给我看看！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101523]]}}}}},
