[7112] = {id = 7112,total_content = 1,content = {[1] = {total_list = 1,list = {[1] = {type = 0,text = [[ 哎，小怪都把你打成这样，这我怎么带你去见老太爷呢！ ]],other = [[]]}}}}},
[7113] = {id = 7113,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 仙家，我追云儿到那边后就失去了妖怪的踪影，能不能拜托您帮我找下，我生要见人，死也要见尸！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101513]]}}}}},
