[7116] = {id = 7116,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 这种小事，看把你愁的。既然卯二娘是给她义子娶亲，她义子不在了或者出了什么事不就成了。 ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101681]]}}}}},
