[7141] = {id = 7141,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 据说白虎岭有辟邪宝物，还请仙家前去帮我大唐寻宝。一旦遇到助纣为虐之人，希望仙家能为苍生除害！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[500030]]}}}}},
[7142] = {id = 7142,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 小仙士真是得道高人，有你在可保百姓平安，但这妖邪之物非一天能铲除，明天还请小仙士前来帮忙！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[500030]]}}}}},
