[8665] = {id = 8665,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 这么快30级了，不如我们再战封魔录，看看现在实力如何！[color]28FF28:第三关通过后将开放心法境界哦！[/color] ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101230]]}}}}},
