[8667] = {id = 8667,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 找到了吗？听说钱为上有很多宝物，说不定去找他有收获。 ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[102210]]}}}}},
[8668] = {id = 8668,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 没错，我就是宝物收藏家！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[102210]]}}}}},
[8669] = {id = 8669,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 宝物被我埋在海边，你先去把海边的巡逻虾兵赶跑。 ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[102220]]}}}}},
[8670] = {id = 8670,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 你要的就是这颗珍珠？帮我把话传达给夜叉族长，我就给你珍珠。 ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[102220]]}}}}},
[8671] = {id = 8671,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 有话跟我说？先打败我的手下，看你够不够格！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[102230]]}}}}},
[8672] = {id = 8672,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 有点实力，不错！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[102230]]}}}}},
[8673] = {id = 8673,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 你说钱为上要在海边捕捞海鲜？这是对大海的大不敬！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[102240]]}}}}},
[8674] = {id = 8674,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 原来是为了追回龙宫宝物，没办法，那就答应他吧！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[102240]]}}}}},
[8675] = {id = 8675,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 答应我的要求了？哈哈哈，这下发大财了！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[102250]]}}}}},
[8676] = {id = 8676,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 可惜你来慢了，东西被人高价买走了。 ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[102250]]}}}}},
[8677] = {id = 8677,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 没办法，商人当然是利益为重，我也无能为力咯。 ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[102260]]}}}}},
[8678] = {id = 8678,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 什么，宝物被买走了？可恶！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[102260]]}}}}},
[8679] = {id = 8679,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 快到处问问有什么线索！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[102270]]}}}}},
[8680] = {id = 8680,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 你说有没有看见过往的富商？ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[102270]]}}}}},
[8681] = {id = 8681,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 好像是又看到一个高富帅路过，你去问问那边的霸道将军。 ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[102280]]}}}}},
[8682] = {id = 8682,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 你说有没有路过的高富帅？ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[102280]]}}}}},
[8683] = {id = 8683,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 喝喝酒杀杀妖怪，也是一件乐事。来送你几张我保留的诛妖帖！[color]28FF28:31级后每天可以领取5次诛妖帖任务哦！[/color] ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[101240]]}}}}},
[8684] = {id = 8684,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 好好使用诛妖帖，能帮你快速成长呢！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[101240]]}}}}},
[8685] = {id = 8685,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 帮我料理以下附近的猪先锋，我就告诉你。 ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[102290]]}}}}},
[8686] = {id = 8686,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 挺有效率嘛！你去问问龙宫侍女吧，她应该看到了。 ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[102290]]}}}}},
[8687] = {id = 8687,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 富商？有富商经过我怎么会看不到呢？ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[102300]]}}}}},
[8688] = {id = 8688,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 先别说这些，靖公主急着找你呢！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[102300]]}}}}},
[8689] = {id = 8689,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 你来了，赶紧帮我赶跑这附近的虎先锋。 ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[102310]]}}}}},
[8690] = {id = 8690,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 你说宝物被买走了，快，我们兵分两路。 ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[102310]]}}}}},
[8691] = {id = 8691,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 去找大唐使者，他消息灵通。 ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[102320]]}}}}},
[8692] = {id = 8692,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 你说富商？我看到了，去长安了。 ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[102320]]}}}}},
[8693] = {id = 8693,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 你说有没有富商回来长安？这种事情你问别人，我哪有空！ ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[102330]]}}}}},
[8694] = {id = 8694,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 我口好渴，什么都不想说。 ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[102330]]}}}}},
[8695] = {id = 8695,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 我口渴了，你快去帮我拿茶水。 ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[102340]]}}}}},
[8696] = {id = 8696,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 茶水我这里大把啊，哈哈！ ]],other = [[]]},[2] = {type = 6,text = [[ 完成任务 ]],other = [[102340]]}}}}},
[8697] = {id = 8697,total_content = 1,content = {[1] = {total_list = 2,list = {[1] = {type = 0,text = [[ 天下没有免费的午餐，去帮我收拾茶水盘，我就给你茶水。 ]],other = [[]]},[2] = {type = 5,text = [[ 触发任务 ]],other = [[102250]]}}}}},
